sources.styles/main.scss:
$super_light: rgb(248, 245, 244);
$light_shades: #EDEAE9;
$light_accent: #6A9F9E;
$main_color: #009432;
$dark_accent: #456169;
$dark_shades: #181E19;
$padding: 2em;
$font-logo: FugazOne;
$font-main: Roboto;

@font-face {
    font-family: 'FugazOne';
    src: url(./fonts/FugazOne-Regular.ttf) format('truetype');
}

@import url('https://fonts.googleapis.com/css?family=Roboto:100,300,500');
@import url('https://fonts.googleapis.com/css?family=Inconsolata');

@mixin for-size($range) {
    $small-phone-upper-boundary: 310px;
    $phone-upper-boundary: 600px;
    $tablet-portrait-upper-boundary: 900px;
    $tablet-landscape-upper-boundary: 1200px;
    $desktop-upper-boundary: 1800px;

    @if $range == small-phone-only {
      @media (max-width: #{$small-phone-upper-boundary -1}) { @content; }
    } @else if $range == phone-only {
      @media (max-width: #{$phone-upper-boundary -1}) { @content; }
    } @else if $range == tablet-portrait-up {
      @media (min-width: $phone-upper-boundary) { @content; }
    } @else if $range == tablet-landscape-up {
      @media (min-width: $tablet-portrait-upper-boundary) { @content; }
    } @else if $range == desktop-up {
      @media (min-width: $tablet-landscape-upper-boundary) { @content; }
    } @else if $range == big-desktop-up {
      @media (min-width: $desktop-upper-boundary) { @content; }
    }
}

body{
    background-color: $dark_shades;
    margin: 0;
}

h1, h2, h3, h4{
    font-family: $font-main;
    font-weight: 400;
}

p{
    font-family: $font-main;
    font-weight: 300;
}

.container {
    display: grid;
    grid-template: auto 1fr auto/repeat(12, 1fr);
    height: 100vh;

    .header {
        grid-column: 1 / -1;
        background: $super_light;
        grid-row: 1 / 2;

    // HEADING
    // Bouton

    .toggle-button {
        position: fixed;
        width: 2em;
        height: 2em;
        padding: 4px;
        transition: 0.25s;
        z-index: 115;
        margin: 1em 2em;
        right: 0;

        @include for-size(tablet-landscape-up){
            display: none;
        }

    }
    .toggle-button:hover {
        cursor: pointer;
    }
    .toggle-button .menu-bar {
        position: absolute;
        border-radius: 2px;
        width: 80%;
        transition: .5s;
    }
        .toggle-button .menu-bar-top {
        border: 4px solid $light_shades;
        border-bottom: none;
        top: 0;
        box-shadow: 1px 1px 5px $dark_shades;
    }
    .toggle-button .menu-bar-middle {
        height: 4px;
        background-color: $light_shades;
        margin-top: 7px;
        margin-bottom: 7px;
        top: 4px;
        box-shadow: 1px 1px 5px $dark_shades;
    }
    .toggle-button .menu-bar-bottom {
        border: 4px solid $light_shades;
        border-top: none;
        top: 22px;
        box-shadow: 1px 1px 5px $dark_shades;
    }
    .button-open .menu-bar-top {
        transform: rotate(45deg) translate(8px, 8px);
        transition: .5s;
        box-shadow: 1px 1px 5px $dark_shades;
    }
    .button-open .menu-bar-middle {
        transform: translate(230px);
        transition: .1s ease-in;
        opacity: 0;
        box-shadow: 1px 1px 5px $dark_shades;
    }
    .button-open .menu-bar-bottom {
        transform: rotate(-45deg) translate(8px, -7px);
        transition: .5s;
        box-shadow: 1px 1px 5px $dark_shades;
    }

    // wrap du menu
    .menu-wrap {
        background-color: $dark_accent;
        position: fixed;
        top: 0;
        height: 100%;
        width: 280px;
        margin-right: -280px;
        font-size: 1em;
        font-weight: 700;
        overflow: auto;
        transition: .25s;
        z-index: 110;
        right: 0;

        @include for-size(tablet-landscape-up){
            margin-right: 0;
            background-color: unset;
        }

        li > a {
            font-family: $font-main;
        }
    }

    .menu-show {
        margin: 0;
        box-shadow: 4px 0px 13px $dark_shades;
    }

        #navigation{
            background: $light_accent;
            grid-column: 1 / -1;
            padding: 1.2em 2em;

            ul {
                grid-column: 1/-1;
                margin-top: 0;
                display: flex;
                flex-flow: column nowrap;
                font-size: 1rem;
                padding: 3em 2rem;
                width: 200px;

                @include for-size(tablet-landscape-up){
                    flex-flow: row nowrap;
                    padding: 1.6em;
                    height: auto;
                    width: auto;
                }
            }

            li{
                display: inline-block;
                margin-bottom: 1em;

                @include for-size(tablet-landscape-up){
                    margin: 0 1em 0 0;
                }

                a{
                    text-decoration: none;
                    color: $super_light;
                    font-family: $font-main;
                    font-weight: 300;

                }
                a:hover{
                    color: $dark_shades;

                }

            }

            span#accueil > a{
                font-family: FugazOne;
                text-decoration: none;
                color: $super_light;
                font-size: 1.5em;

                @include for-size(tablet-landscape-up){
                    padding: 1em 0 0 5.3em;
                }

                @include for-size(desktop-up){
                    padding: 1em 0 0 12em;
                }
            }
        }
    }

    .presentation--titre{

        display: grid;
        grid-template: repeat(1, 1fr) / repeat(3, 1fr);

        #main--title{
            background: transparent;
            grid-column: 1 / -1;
            height: 3em;
            margin: 0;
            padding: 1em 0 0 1em;
            position: absolute;
            color: $light_shades;

            @include for-size(tablet-landscape-up){
                padding: 1em 0 0 5em;
            }

            @include for-size(desktop-up){
                padding: 1em 0 0 10em;
            }

        }

        p {
            background: $dark_accent;
            margin: auto 0;
            padding: 5em 2em 2em 2em;
            grid-column: 1 / 4;
            color: $super_light;

            @include for-size(small-phone-upper-boundary){
                grid-column: 1 / 4;
            }

            @include for-size(tablet-landscape-up){
                padding: 5em 2em 2em 10em;
            }

            @include for-size(desktop-up){
                padding: 5em 2em 2em 20em;
            }
        }
    }

    .container--photos{
        height: 10em;
        background: $super_light;

        p{
            text-align: center;

        }
    }

    .presentation--docks{
        grid-column: 1 / -1;
        grid-row: 2 / 3;
        padding: 2em $padding;
        background: $light_shades;

        @include for-size(tablet-landscape-up){
            padding: 2em 10em;
        }

        @include for-size(desktop-up){
            padding: 2em 20em;
        }

        .citation{
            max-width: 600px;
        }

        .button-primary{
            background: $light_accent;
            border: none;
            padding: 1em 2em;
            font-size: 1em;
            color: $light_shades;
            cursor: pointer;
            border-radius: .4em;

            &:hover{
                background-color: $dark_accent;
            }
        }

    }

    .contact{
        grid-column: 1 / -1;
        grid-row: 3 / 4;
        padding: $padding;
        background: $light_shades;
    }

    .footer {
        grid-column: 1 / -1;
        grid-row: 3 / 4;
        padding: 0 1em;
        background: $dark_shades;

        @include for-size(tablet-landscape-up){
            padding: 0 5em;
        }

        @include for-size(desktop-up){
            padding: 0 10em;
        }

        #copyright{
            font-family: 'Inconsolata';
            text-align: right;
            color: $light_shades;
        }
    }
}
